import "./App.css";
import CreateInputForm from "./components/CreateInputForm";
import CreateTable from "./components/CreateTable";
import { useState } from "react";

function App() {
  // const initUsers = [
  //   { id: Math.random(), index: 1, name: "dima", age: 20 },
  //   { id: Math.random(), index: 2, name: "oleg", age: 25 },
  //   { id: Math.random(), index: 3, name: "max", age: 22 },
  //   { id: Math.random(), index: 4, name: "vlad", age: 27 },
  //   { id: Math.random(), index: 5, name: "vitya", age: 25 },
  //   { id: Math.random(), index: 6, name: "nikita", age: 42 },
  //   { id: Math.random(), index: 7, name: "masha", age: 53 },
  //   { id: Math.random(), index: 8, name: "olya", age: 42 },
  //   { id: Math.random(), index: 9, name: "sasha", age: 71 },
  //   { id: Math.random(), index: 10, name: "lera", age: 68 },
  // ];

  const [userList, setUserList] = useState(createUsersList(100));

  const createUser = (newUser) => {
    setUserList([...userList, newUser]);
  };
  const removeUser = (user) => {
    setUserList(userList.filter((users) => users.id !== user.id));
  };

  const sortUsers = (value) => {
    setUserList([...userList].sort((a, b) => (a[value] < b[value] ? -1 : 1)));
  };

  return (
    <div className="App">
      <CreateInputForm createUser={createUser} userList={userList} />
      {userList.length !== 0 ? (
        <CreateTable userList={userList} remove={removeUser} sort={sortUsers} />
      ) : (
        <div style={{ margin: "50px" }}>
          The data is empty, please add a new user to create a table
        </div>
      )}
    </div>
  );
}

export default App;
