import React, { useState } from "react";
import Table from "../components/UI/Table/Table";
import ButtonTableHead from "../components/UI/Button/ButtonTableHead";
import PagesList from "../components/PagesList";
import Button from "../components/UI/Button/Button";
import ModalWindow from "../components/UI/Modal/ModalWindow";
import CreateInputForm from "../components/CreateInputForm";

const MainPage = ({
  userList,
  remove,
  requestSort,
  currentPage,
  setCurrentPage,
  totalPage,
  modal,
  setModal,
  createUser,
}) => {
  const [users, setUsers] = useState([]);
  const [value, setValue] = useState({ name: "", email: "" });

  const addNewUser = () => {
    const newUser = {
      ...value,
      id: users.length + 1,
      index: users.length + 1,
    };
    createUser(newUser);
    setValue({ name: "", email: "" });
  };

  const renderList = users.map((users) => (
    <tr key={users.id}>
      <td>{users.id}</td>
      <td>{users.name}</td>
      <td>{users.email}</td>
      <td>
        <Button
          style={{
            cursor: "pointer",
            margin: 0,
            padding: "3px",
            border: "1px solid grey",
          }}
          onClick={() => remove(users)}
        >
          del
        </Button>
      </td>
    </tr>
  ));

  return (
    <div>
      <Table>
        <caption style={{ fontSize: "30px", marginTop: "30px" }}>
          Table of users
        </caption>
        <thead>
          <tr>
            <th>
              <ButtonTableHead onClick={() => requestSort("id")}>
                <span>Number </span>
              </ButtonTableHead>
            </th>
            <th>
              <ButtonTableHead onClick={() => requestSort("name")}>
                <span>Name </span>
              </ButtonTableHead>
            </th>
            <th colSpan={2}>
              <ButtonTableHead onClick={() => requestSort("email")}>
                <span>Email</span>
              </ButtonTableHead>
            </th>
          </tr>
        </thead>
        <tbody>{renderList}</tbody>
      </Table>
      <PagesList
        currentPage={currentPage}
        setCurrentPage={setCurrentPage}
        totalPage={totalPage}
      />
      <ModalWindow visible={modal} setVisible={setModal}>
        <CreateInputForm createUser={createUser} userList={userList} />
      </ModalWindow>
      <Button onClick={() => setModal(true)}>Create new user</Button>
    </div>
  );
};

export default MainPage;
