import React, { useState } from "react";
import Input from "./UI/Input";
import Button from "./UI/Button";

const CreateInputForm = ({ createUser, userList }) => {
  const [value, setValue] = useState({ name: "", age: "" });

  const addNewUser = () => {
    const newUser = { ...value, id: Math.random(), index: userList.length + 1 };
    createUser(newUser);
    setValue({ name: "", age: "" });
  };
  return (
    <div style={{ padding: "0 20%" }}>
      <Input
        value={value.name}
        onChange={(e) => setValue({ ...value, name: e.target.value })}
        type="text"
        placeholder={"Enter the name of new users"}
      />
      <Input
        value={value.age}
        onChange={(e) => setValue({ ...value, age: e.target.value })}
        type="text"
        placeholder={"Enter the age of new users"}
      />
      <Button onClick={addNewUser}>add new user</Button>
    </div>
  );
};

export default CreateInputForm;
