import Table from "./UI/Table";
import ButtonTableHead from "./UI/ButtonTableHead";
import React from "react";

const CreateTable = ({
  userList,
  remove,
  setFilter,
  filter,
  sortedUsersList,
}) => {
  if (!userList.length) {
    return <div style={{ margin: "50px" }}>Nothing found</div>;
  }
  const renderList = userList.map((user) => (
    <tr key={user.id}>
      <td>{user.index}</td>
      <td>{user.name}</td>
      <td>{user.age}</td>
      <td>
        <button onClick={() => remove(user)}>del</button>
      </td>
    </tr>
  ));

  return (
    <Table>
      <caption style={{ fontSize: "30px", marginTop: "30px" }}>
        Table of users
      </caption>
      <thead>
        <tr>
          <th>
            <ButtonTableHead
              onClick={() => sortedUsersList({ ...filter, sort: "index" })}
            >
              Number
            </ButtonTableHead>
          </th>
          <th>
            <ButtonTableHead
              onClick={() => sortedUsersList({ ...filter, sort: "name" })}
            >
              Name
            </ButtonTableHead>
          </th>
          <th colSpan={2}>
            <ButtonTableHead onClick={() => sortedUsersListt("age")}>
              Age
            </ButtonTableHead>
          </th>
        </tr>
      </thead>
      <tbody>{renderList}</tbody>
    </Table>
  );
};

export default CreateTable;
