import Table from "./UI/Table";
import ButtonTableHead from "./UI/ButtonTableHead";
import React, { useState } from "react";

const CreateTable = ({ userList, remove }) => {
  const renderList = userList.map((user, index) => (
    <tr key={user.id}>
      <td>{index + 1}</td>
      <td>{user.name}</td>
      <td>{user.age}</td>
      <td>
        <button onClick={() => remove(user)}>del</button>
      </td>
    </tr>
  ));

  return (
    <Table>
      <caption style={{ fontSize: "30px" }}>Table of users</caption>
      <thead>
        <tr>
          <th>
            <ButtonTableHead onClick={() => setSort("Number")}>
              Number
            </ButtonTableHead>
          </th>
          <th>
            <ButtonTableHead onClick={() => setSort("Name")}>
              Name
            </ButtonTableHead>
          </th>
          <th colSpan={2}>
            <ButtonTableHead onClick={() => setSort("Age")}>
              Age
            </ButtonTableHead>
          </th>
        </tr>
      </thead>
      <tbody>{renderList}</tbody>
    </Table>
  );
};

export default CreateTable;
