import "./App.css";
import CreateInputForm from "./components/CreateInputForm";
import CreateTable from "./components/CreateTable";
import { useMemo, useState } from "react";
import { createUsersList } from "./components/createUsersList";
import Input from "./components/UI/Input";
import FilterUsers from "./components/FilterUsers";

function App() {
  const [userList, setUserList] = useState(createUsersList(10));

  const [filter, setFilter] = useState({ sort: "", search: "" });

  const [sort, setSort] = useState("");

  const [search, setSearch] = useState("");

  const sortedUsersList = useMemo(() => {
    return [...userList].sort((a, b) => (a[sort] < b[sort] ? -1 : 1));
  }, [sort, userList]);

  const searchAndSort = useMemo(() => {
    return sortedUsersList.filter((users) =>
      users.name.toLowerCase().includes(search)
    );
  }, [search, sortedUsersList]);

  const createUser = (newUser) => {
    setUserList([...userList, newUser]);
  };
  const removeUser = (user) => {
    setUserList(userList.filter((users) => users.id !== user.id));
  };

  return (
    <div className="App">
      <CreateInputForm createUser={createUser} userList={userList} />
      <FilterUsers />
      <CreateTable
        userList={searchAndSort}
        remove={removeUser}
        sort={setSort}
      />
    </div>
  );
}

export default App;
