import "./App.css";
import { useState } from "react";
import Button from "./components/UI/Button";
import Input from "./components/UI/Input";
import Table from "./components/UI/Table";

function App() {
  const initUsers = [
    { id: Math.random(), name: "dima", age: 20 },
    { id: Math.random(), name: "dima2", age: 25 },
    { id: Math.random(), name: "dima3", age: 22 },
    { id: Math.random(), name: "dima4", age: 27 },
    { id: Math.random(), name: "dima5", age: 25 },
    { id: Math.random(), name: "dima6", age: 42 },
    { id: Math.random(), name: "dima7", age: 53 },
    { id: Math.random(), name: "dima8", age: 42 },
    { id: Math.random(), name: "dima9", age: 71 },
    { id: Math.random(), name: "dima10", age: 68 },
  ];

  const [userList, setUserList] = useState(initUsers);
  const [value, setValue] = useState({ name: "", age: "" });

  const removeUser = (value) => {
    setUserList(userList.filter((user) => user.id !== value.id));
  };



  const renderList = userList.map((user, index) => (
    <tr key={user.id}>
      <td>{index + 1}</td>
      <td>{user.name}</td>
      <td>{user.age}</td>
    </tr>
  ));

  const addNewUser = () => {
    setUserList([...userList, { ...value, id: Math.random() }]);
    setValue({ name: "", age: "" });
  };

  return (
    <div className="App">
      <div>
        <Input
          value={value.name}
          onChange={(e) => setValue({ ...value, name: e.target.value })}
          type="text"
          placeholder={"Enter the name of new users"}
        />
        <Input
          value={value.age}
          onChange={(e) => setValue({ ...value, age: e.target.value })}
          type="text"
          placeholder={"Enter the age of new users"}
        />
        <Button onClick={addNewUser}>add new user</Button>
      </div>
      <Table>
        <thead>
          <tr>
            <th>Number</th>
            <th>Name</th>
            <th>Age</th>
          </tr>
        </thead>
        <tbody>{renderList}</tbody>
      </Table>
    </div>
  );
}

export default App;
