import "./App.css";
import CreateInputForm from "./components/CreateInputForm";
import CreateTable from "./components/CreateTable";
import { useState } from "react";

function App() {
  const initUsers = [
    { id: Math.random(), index: "", name: "dima", age: 20 },
    { id: Math.random(), index: "", name: "oleg", age: 25 },
    { id: Math.random(), index: "", name: "max", age: 22 },
    { id: Math.random(), index: "", name: "vlad", age: 27 },
    { id: Math.random(), index: "", name: "vitya", age: 25 },
    { id: Math.random(), index: "", name: "nikita", age: 42 },
    { id: Math.random(), index: "", name: "masha", age: 53 },
    { id: Math.random(), index: "", name: "olya", age: 42 },
    { id: Math.random(), index: "", name: "sasha", age: 71 },
    { id: Math.random(), index: "", name: "lera", age: 68 },
  ];

  const [userList, setUserList] = useState(initUsers);

  const createUser = (newUser) => {
    setUserList([...userList, newUser]);
  };
  const removeUser = (user) => {
    setUserList(userList.filter((users) => users.id !== user.id));
  };

  const sortUsers = (value) => {
    setUserList([...userList].sort((a, b) => (a[value] > b[value] ? -1 : 1)));
  };

  return (
    <div className="App">
      <CreateInputForm createUser={createUser} />
      {userList.length !== 0 ? (
        <CreateTable userList={userList} remove={removeUser} sort={sortUsers} />
      ) : (
        <div style={{ margin: "50px" }}>
          The data is empty, please add a new user to create a table
        </div>
      )}
    </div>
  );
}

export default App;
