import "./App.css";
import CreateInputForm from "./components/CreateInputForm";
import CreateTable from "./components/CreateTable";
import { useState } from "react";

function App() {
  const initUsers = [
    { id: Math.random(), name: "dima", age: 20 },
    { id: Math.random(), name: "dima2", age: 25 },
    { id: Math.random(), name: "dima3", age: 22 },
    { id: Math.random(), name: "dima4", age: 27 },
    { id: Math.random(), name: "dima5", age: 25 },
    { id: Math.random(), name: "dima6", age: 42 },
    { id: Math.random(), name: "dima7", age: 53 },
    { id: Math.random(), name: "dima8", age: 42 },
    { id: Math.random(), name: "dima9", age: 71 },
    { id: Math.random(), name: "dima10", age: 68 },
  ];

  const [userList, setUserList] = useState(initUsers);
  // const removeUser = (value) => {
  //   setUserList(userList.filter((user) => user.id !== value.id));
  // };

  const createUser = (newUser) => {
    setUserList([...userList, newUser]);
  };
  const removeUser = (user) => {
    setUserList(userList.filter((users) => users.id !== user.id));
  };

  return (
    <div className="App">
      <CreateInputForm createUser={createUser} />
      {userList.length !== 0 ? (
        <CreateTable userList={userList} remove={removeUser} />
      ) : (
        <div>The data is empty</div>
      )}
    </div>
  );
}

export default App;
