import "./App.css";
import { useState } from "react";

function App() {
  const initUsers = [
    { id: Math.random().toFixed(2), name: "dima", age: 20 },
    { id: Math.random().toFixed(2), name: "dima2", age: 25 },
    { id: Math.random().toFixed(2), name: "dima3", age: 22 },
    { id: Math.random().toFixed(2), name: "dima4", age: 27 },
    { id: Math.random().toFixed(2), name: "dima5", age: 25 },
    { id: Math.random().toFixed(2), name: "dima6", age: 42 },
    { id: Math.random().toFixed(2), name: "dima7", age: 53 },
    { id: Math.random().toFixed(2), name: "dima8", age: 42 },
    { id: Math.random().toFixed(2), name: "dima9", age: 71 },
    { id: Math.random().toFixed(2), name: "dima10", age: 68 },
  ];

  const [userList, setUserList] = useState(initUsers);
  const [value, setValue] = useState({ userName: "", userAge: "" });

  const renderList = userList.map((user, index) => (
    <tr>
      <td>{index + 1}</td>
      <td>{user.name}</td>
      <td>{user.age}</td>
    </tr>
  ));

  const addNewUser = () => {
    setUserList([...userList, { ...value, id: Math.random().toFixed(2) }]);
    setValue({ userName: "", userAge: "" });
  };

  return (
    <div className="App">
      <div>
        <input
          value={value.userName}
          onChange={(e) => setValue({ ...value, userName: e.target.value })}
          type="text"
          placeholder={"Enter the name of new users"}
        />
        <input
          value={value.userAge}
          onChange={(e) => setValue({ ...value, userAge: e.target.value })}
          type="text"
          placeholder={"Enter the age of new users"}
        />
        <button onClick={() => setUserList([...userList, addNewUser])}>
          add new user
        </button>
      </div>
      <table>
        <thead>
          <tr>
            <th>Number</th>
            <th>Name</th>
            <th>Age</th>
          </tr>
        </thead>
        <tbody>{renderList}</tbody>
      </table>
    </div>
  );
}

export default App;
