import "./App.css";
import { useState } from "react";

function App() {
  const initUsers = [
    { id: Math.random().toFixed(2), name: "dima", age: 20 },
    { id: Math.random().toFixed(2), name: "dima2", age: 25 },
    { id: Math.random().toFixed(2), name: "dima3", age: 22 },
    { id: Math.random().toFixed(2), name: "dima4", age: 27 },
    { id: Math.random().toFixed(2), name: "dima5", age: 25 },
    { id: Math.random().toFixed(2), name: "dima6", age: 42 },
    { id: Math.random().toFixed(2), name: "dima7", age: 53 },
    { id: Math.random().toFixed(2), name: "dima8", age: 42 },
    { id: Math.random().toFixed(2), name: "dima9", age: 71 },
    { id: Math.random().toFixed(2), name: "dima10", age: 68 },
  ];

  const [list, setList] = useState(initUsers);
  const [value, setValue] = useState({ name: "", age: "" });

  const renderList = list.map((user, index) => (
    <tr>
      <td>{index + 1}</td>
      <td>{user.name}</td>
      <td>{user.age}</td>
    </tr>
  ));

  const addNewUser = () => {
    const newUser = {
      id: Math.random().toFixed(2),
      name: value.name,
      age: value.age,
    };
    setList([...list, newUser]);
  };

  return (
    <div className="App">
      <div>
        <input
          value={value.name}
          onChange={(e) => setValue({ ...value, name: e.target.value })}
          type="text"
          placeholder={"Enter the name of new users"}
        />
        <input
          value={value.age}
          onChange={(e) => setValue({ ...value, age: e.target.value })}
          type="text"
          placeholder={"Enter the age of new users"}
        />
        <button onClick={() => setList([...list, addNewUser])}>
          add new user
        </button>
      </div>
      <table>
        <thead>
          <tr>
            <th>Number</th>
            <th>Name</th>
            <th>Age</th>
          </tr>
        </thead>
        <tbody>{renderList}</tbody>
      </table>
    </div>
  );
}

export default App;
