import "./App.css";
import CreateInputForm from "./components/CreateInputForm";
import CreateTable from "./components/CreateTable";
import { useEffect, useState } from "react";
import { createUsersList } from "./components/createUsersList";
import FilterUsers from "./components/FilterUsers";
import ModalWindow from "./components/UI/ModalWindow";
import Button from "./components/UI/Button";
import { useSortAndSearch } from "./hooks/useSortAndSearch";
import { usePagination } from "./hooks/usePagination";
import axios from "axios";

function App() {
  const getData = async () => {
    const response = await axios.get(
      "https://jsonplaceholder.typicode.com/comments"
    );
    setUserList(response.data);
  };
  const createUsersList = (userList) => {
    let arr = [];
    for (let i = 0; i < userList; i++) {
      arr.push({
        id: userList.id,
        index: i + 1,
        name: userList.name,
        age: userList.email,
      });
    }
    return arr;
  };
  const [userList, setUserList] = useState(createUsersList(getData()));
  // const [userList, setUserList] = useState(createUsersList(100));
  const [filter, setFilter] = useState({
    direction: "",
    key: null,
    search: "",
  });
  const [modal, setModal] = useState(false);
  const searchAndSort = useSortAndSearch(userList, filter);

  const [totalCount, setTotalCount] = useState(userList.length);
  const [limit, setLimit] = useState(10);
  const [currentPage, setCurrentPage] = useState(1);

  const getPageCount = (totalPage, limit) => {
    return Math.ceil(totalPage / limit);
  };

  useEffect(() => {
    createUsersList(getData());
  }, []);

  const totalPages = getPageCount(totalCount, limit);
  let pagesArray = usePagination(totalPages, totalCount);

  console.log(pagesArray);

  const requestSort = (key) => {
    let direction = "ascending";
    if (filter && filter.key === key && filter.direction === "ascending") {
      direction = "descending";
    }
    setFilter({ ...filter, key: key, direction: direction });
  };

  const createUser = (newUser) => {
    setUserList([...userList, newUser]);
    setModal(false);
  };
  const removeUser = (user) => {
    setUserList(userList.filter((users) => users.id !== user.id));
  };

  return (
    <div className="App">
      <Button onClick={() => setModal(true)}>Create new user</Button>
      <ModalWindow visible={modal} setVisible={setModal}>
        <CreateInputForm createUser={createUser} userList={userList} />
      </ModalWindow>
      <FilterUsers filter={filter} setFilter={setFilter} />
      <CreateTable
        userList={searchAndSort}
        remove={removeUser}
        requestSort={requestSort}
      />
      {pagesArray.map((page) => (
        <Button onClick={() => setCurrentPage(page)} key={page}>
          {page}
        </Button>
      ))}
    </div>
  );
}

export default App;
