import "./App.css";
import CreateInputForm from "./components/CreateInputForm";
import CreateTable from "./components/CreateTable";
import { useState } from "react";

function App() {
  const initUsers = [
    { id: Math.random(), name: "dima", age: 20 },
    { id: Math.random(), name: "oleg", age: 25 },
    { id: Math.random(), name: "max", age: 22 },
    { id: Math.random(), name: "vlad", age: 27 },
    { id: Math.random(), name: "vitya", age: 25 },
    { id: Math.random(), name: "nikita", age: 42 },
    { id: Math.random(), name: "masha", age: 53 },
    { id: Math.random(), name: "olya", age: 42 },
    { id: Math.random(), name: "sasha", age: 71 },
    { id: Math.random(), name: "lera", age: 68 },
  ];

  const [userList, setUserList] = useState(initUsers);

  const createUser = (newUser) => {
    setUserList([...userList, newUser]);
  };
  const removeUser = (user) => {
    setUserList(userList.filter((users) => users.id !== user.id));
  };

  // const sortUsers = () => {
  //   console.log([...userList].sort((a, b) => (a[value] < b[value] ? -1 : 1)));
  // };

  const [sort, setSort] = useState(null);
  if (sort !== null) {
    setUserList(
      [...userList].sort((a, b) => {
        if (a[sort] < b[sort]) {
          return -1;
        }
        if (a[sort] > b[sort]) {
          return 1;
        }
        return 0;
      })
    );
  }

  return (
    <div className="App">
      <CreateInputForm createUser={createUser} />
      {userList.length !== 0 ? (
        <CreateTable userList={userList} remove={removeUser} sort={setSort} />
      ) : (
        <div style={{ margin: "50px" }}>
          The data is empty, please add a new user to create a table
        </div>
      )}
    </div>
  );
}

export default App;
